mapper.update:
UPDATE achievement_target_detail_${eid}

    
        
            
                when #{item.id,jdbcType=BIGINT} then #{item.achievementTarget,jdbcType=DECIMAL}
            
        
        when '000' then achievement_target
        else achievement_target
    
    
        
            
                when #{item.id,jdbcType=BIGINT} then #{item.editTime,jdbcType=TIMESTAMP}
            
        
        when '000' then edit_time
        else edit_time
    
    
        
            
                when #{item.id,jdbcType=BIGINT} then #{item.updateUserId,jdbcType=VARCHAR}
            
        
        when '000' then update_user_id
        else update_user_id
    
    
        
            
                when #{item.id,jdbcType=BIGINT} then #{item.updateUserName,jdbcType=VARCHAR}
            
        
        when '000' then update_user_name
        else update_user_name
    
    
        
            
                when #{item.id} then #{item.storeName,jdbcType=VARCHAR}
            
        
        when '000' then store_name
        else store_name
    
    
        
            
                when #{item.id} then #{item.storeNum,jdbcType=VARCHAR}
            
        
        when '000' then store_num
        else store_num
    

where id in

    #{item.id}

mapper.select:
SELECT sum(achievement_target)
FROM achievement_target_detail_${eid}
WHERE
    region_path LIKE concat(#{regionPath},'%')
  AND time_type = #{timeType}
  AND begin_date = #{beginDate}

SELECT id,
       store_id,
       achievement_target,
       begin_date
FROM achievement_target_detail_${eid}
WHERE store_id = #{entity.storeId}
  AND begin_date = #{entity.beginDate}
  AND time_type = #{entity.timeType}

SELECT id,
store_id,
store_name,
achievement_target,
begin_date,
target_id
FROM achievement_target_detail_${eid}
WHERE
 begin_date = #{entity.beginDate}
AND time_type = #{entity.timeType}
AND store_id in

    #{item}

select sum(achievement_target)
from achievement_target_detail_${eid}
where begin_date = #{currentMonth}

select ifnull(sum(achievement_target), 0)
from achievement_target_detail_${eid}
where begin_date >= #{beginTime}
  and #{endTime} >= end_date
  AND store_id = #{storeId}

select store_id,begin_date,achievement_target
from achievement_target_detail_${eid}
WHERE
achievement_year = #{achievementYear}
AND store_id IN

    #{storeId}

GROUP BY store_id,begin_date
