mapper.update:
UPDATE achievement_target_${eid}

    
        
            
                when #{item.id,jdbcType=BIGINT} then #{item.yearAchievementTarget,jdbcType=DECIMAL}
            
        
        when '000' then year_achievement_target
        else year_achievement_target
    
    
        
            
                when #{item.id,jdbcType=BIGINT} then #{item.editTime,jdbcType=TIMESTAMP}
            
        
        when '000' then edit_time
        else edit_time
    
    
        
            
                when #{item.id,jdbcType=BIGINT} then #{item.updateUserId,jdbcType=VARCHAR}
            
        
        when '000' then update_user_id
        else update_user_id
    
    
        
            
                when #{item.id,jdbcType=BIGINT} then #{item.updateUserName,jdbcType=VARCHAR}
            
        
        when '000' then update_user_name
        else update_user_name
    
    
        
            
                when #{item.id,jdbcType=BIGINT} then #{item.storeName,jdbcType=VARCHAR}
            
        
        when '000' then store_name
        else store_name
    
    
        
            
                when #{item.id,jdbcType=BIGINT} then #{item.storeNum,jdbcType=VARCHAR}
            
        
        when '000' then store_num
        else store_num
    

where id in

    #{item.id}

mapper.select:
SELECT id, #{eid} as eid, store_id, store_name, achievement_year, year_achievement_target, edit_time, region_id FROM achievement_target_${eid}
store_id in
#{item}
AND region_path LIKE concat(#{regionPath},'%')
AND region_id =#{regionId}
AND achievement_year=#{achievementYear}
AND (store_name LIKE concat('%',#{storeName},'%') or store_num LIKE concat('%',#{storeName},'%'))
order by edit_time desc

SELECT id, store_id, store_name, achievement_year, year_achievement_target, edit_time FROM achievement_target_${eid}
id in
#{item}
order by edit_time desc

SELECT id, achievement_target, begin_date, end_date, time_type, target_id FROM achievement_target_detail_${eid}
target_id in
#{item}
ORDER BY begin_date
