mapper.select:
SELECT id, name, update_user_name, edit_time, locked
from achievement_type_${eid}
ORDER BY id desc

SELECT id, name, update_user_name, edit_time, locked
from achievement_type_${eid}
where id = #{id}

select *
from achievement_type_${enterpriseId}
where id in (

    #{id}

)

mapper.update:
update achievement_type_${enterpriseId}
set locked = 1
where id = #{id}

mapper.delete:
update achievement_type_${eid}
set deleted = 1
where id = #{id}

mapper.select:
select count(1)
from achievement_type_${eid}
where name=#{name}

    and id != #{id}

and deleted = 0
