mapper.select:
select

from enterprise_auth_device
where 
  enterprise_id = #{enterpriseId} and device_id = #{deviceId}

  and channel_no = #{channelNo}


  and app_id = #{appId}

order by id desc

update.foreach:
update
  enterprise_auth_device
set
  auth_status = #{cancel.authStatus}, cancel_auth_time = #{cancel.cancelAuthTime}
where id = #{cancel.id}

mapper.select:
 select
 
 from
   enterprise_auth_device
 where
   enterprise_id = #{enterpriseId}
 
   and (device_id like concat('%',#{param.keywords},'%') or device_name like concat('%',#{param.keywords},'%'))
 

  and app_id = #{param.appId}

 
   and auth_status = #{param.authStatus}
 
 order by id desc
