mapper.delete:
delete  from
user_region_mapping_${enterpriseId}
where
user_id = #{userId}

    and region_id in
    
        #{regionId}

delete from user_region_mapping_${enterpriseId} where user_id in

    #{userId}

and source != 'create'

mapper.insert:
INSERT into user_region_mapping_${enterpriseId}
(
region_id,user_id,create_id,create_time,update_id,update_time,source
) VALUES (
#{entity.regionId, jdbcType=VARCHAR},
#{entity.userId, jdbcType=VARCHAR},
#{entity.createId, jdbcType=VARCHAR},
UNIX_TIMESTAMP(NOW())*1000,
#{entity.updateId, jdbcType=VARCHAR},
UNIX_TIMESTAMP(NOW())*1000,
#{entity.source, jdbcType=VARCHAR}
)

mapper.select:
select 

from user_region_mapping_${enterpriseId}

    where user_id in
    
        #{userId}

select

from user_region_mapping_${enterpriseId}

    where region_id in
    
        #{regionId}

select
count(1) as userNum
from user_region_mapping_${enterpriseId}

    where region_id in
    
        #{regionId}

select region_id as regionId , count(*) as userCount
 from user_region_mapping_${enterpriseId}

    where region_id in
    
        #{regionId}
    

group by regionId;

mapper.delete:
delete  from
user_region_mapping_${enterpriseId}
where 1= 1

    and user_id in
    
        #{userId}
    


    and region_id in
    
        #{regionId}

mapper.select:
select
user_id as userId,
region_id as regionId
from
user_region_mapping_${enterpriseId}
where 1= 1

    and user_id in
    
        #{userId}
    


    and region_id in
    
        #{regionId}
