mapper.update:
update import_task_${eid}
set status = #{task.status}

    , file_url = #{task.fileUrl}


    , remark = #{task.remark}


    , success_num = #{task.successNum}


    , total_num = #{task.totalNum}


    , file_name = #{task.fileName}

where id = #{task.id}

mapper.select:
select id, file_name as fileName, status, file_type as fileType, is_import as isImport, file_url as fileUrl, create_name as createName, create_time as createTime, remark, success_num as successNum, total_num as totalNum from import_task_${eid} where create_user_id = #{userId}
and file_type = #{fileType}
and is_import = #{isImport}
and status = #{status}
order by create_time desc

select
    unique_field as uniqueField,
    unique_name as uniqueName
from import_distinct_${eid}
where file_type = #{fileType}
