mapper.insert:
insert into sys_role_menu_v2_${eid}
(
menu_id,
role_id
)
VALUES

    (
    #{item.menuId},
    #{item.roleId}
    )

mapper.select:
select  * from
sys_role_menu_${eid}
where role_id=#{roleId} and platform=#{platform}

select  * from
sys_role_menu_v2_${eid}
where role_id=#{roleId} and platform=#{platform}

select  * from
sys_role_menu_v2_${eid}
where platform=#{platform}
order by id asc

