mapper.select:
select
  id, quick_column_id as quickColumnId,
  reason_name AS reasonname, mapping_result as mappingResult
from tb_meta_quick_column_reason_${enterpriseId}
where quick_column_id = #{columnId}
  and deleted = 0

mapper.update:
update tb_meta_quick_column_reason_${enterpriseId}
set deleted = 1
where quick_column_id in

    #{id}

mapper.select:
select
  id
from tb_meta_quick_column_reason_${enterpriseId}
where quick_column_id = #{columnId}
  and deleted = 0

select reason_name
from tb_meta_quick_column_reason_${enterpriseId}
where quick_column_id=#{columnId}
AND mapping_result=#{mappingResult}
AND deleted=0
