mapper.insert:
insert into store_user_collect_${map.eid}
(store_id, user_id, status, create_time)
values
(
    #{map.storeId}, #{map.userId}, #{map.status}, #{map.createTime}
)

mapper.update:
update store_user_collect_${map.eid}
set status = #{map.status}
where store_id = #{map.storeId} and user_id = #{map.userId}

mapper.select:
        select
        store_id as storeId,
        user_id  as userId,
        status as status
         from store_user_collect_${eid}
        
            
                user_id=#{userId}
            
            
                
                    #{storeId}
                
            
        

        and status=1
