mapper.select:
select 

from supervision_store_task_${enterpriseId}
where id = #{id,jdbcType=BIGINT}

mapper.update:
update supervision_store_task_${enterpriseId}
set cancel_status = 1

  
    and task_parent_id = #{taskParentId,jdbcType=BIGINT}
  
  
    and supervision_task_id = #{supervisionTaskId,jdbcType=BIGINT}
  
  
    and id = #{id,jdbcType=BIGINT}

mapper.select:
select count(1)
from supervision_store_task_${enterpriseId}
where supervision_task_id = #{supervisionTaskId}
and cancel_status = 0

mapper.update:
update supervision_store_task_${enterpriseId}
set deleted = 1
where task_parent_id = #{parentId,jdbcType=BIGINT}

mapper.select:
select from supervision_store_task_${enterpriseId}
and task_parent_id = #{parentId}
and supervision_task_id = #{supervisionTaskId}
#{storeId}
region_path like concat('%/', #{regionId}, '/%')
and supervision_handle_user_name like concat('%',#{userName},'%')
and (handle_over_time_status = #{handleOverTimeStatus} or (task_state = 0 and handle_over_time_status = 0 and task_end_time < now()))
and ((handle_over_time_status = 0 AND task_state != 0) or (task_state=0 and task_end_time > now()) )
(task_state = 0 and cancel_status = 0 )
( task_state = 1 and cancel_status = 0)
( task_state = 4 and cancel_status = 0)
( cancel_status = 1)
order by id desc

select
count(1)
from supervision_store_task_${enterpriseId}
where  supervision_task_id = #{taskId}
and task_state = 0
and cancel_status= 0

select

from supervision_store_task_${enterpriseId}
where  supervision_task_id = #{taskId}
and (task_state = 1 or task_state = 3)
order by submit_time desc
limit 1

select ,
TIMESTAMPDIFF(second,now(),task_end_time) AS cnt
from supervision_store_task_${enterpriseId}
where supervision_handle_user_id = #{query.supervisionUserId}
and deleted = 0 and cancel_status = 0

  and store_id = #{query.storeId}


  and task_state = 0


  and task_state = #{taskStatus}


  and task_state = 4


  and cancel_status = 1


  and task_end_time BETWEEN  #{startTime} and #{endTime}


  and priority = #{priority}


  and (handle_over_time_status = #{handleOverTimeStatus} or (task_state = 0 and handle_over_time_status = 0 and task_end_time  <  now()))


  and ((handle_over_time_status = 0 AND task_state != 0) or (task_state=0 and  task_end_time > now()) )

ORDER BY
if(cnt >= 0, 0, 1),
if(cnt  <  0, abs(cnt), cnt )

select
store_id as storeId,
ifnull(sum(case when task_state = 0 and cancel_status = 0 and deleted = 0  then 1 else 0 end),0) as count
from supervision_store_task_${enterpriseId}
where supervision_handle_user_id = #{userId}
group by store_id order by count desc

