mapper.select:
select

from supervision_store_task_${enterpriseId}

  
    and task_state = 0
  
  
    and cancel_status = 0
  
  
    
      #{supervisionTaskId}

select
count(1)
from supervision_store_task_${enterpriseId}

  
    
      #{supervisionTaskId}

select

from supervision_store_task_${enterpriseId}
where task_state = 0 and cancel_status = 0
and reminder_time_before_starting = #{reminderTimeBeforeStarting}

select

from supervision_store_task_${enterpriseId}
where task_state = 0 and cancel_status = 0
and reminder_time_before_end = #{reminderTimeAfterEnd}

select supervision_task_id as supervisionTaskId, ifnull(sum(case when cancel_status = 0 then 1 else 0 end),0) filterCancelCount, count(1) as count, max(submit_time) as maxSubmitTime, max(complete_time) as maxCompleteTime, ifnull(sum(case when task_state = 0 and cancel_status = 0 then 1 else 0 end),0) handleNum, ifnull(sum(case when task_state = 1 and cancel_status = 0 then 1 else 0 end),0) completeNum, ifnull(sum(case when task_state = 4 and cancel_status = 0 then 1 else 0 end),0) approveNum, ifnull(sum(case when (handle_over_time_status = 1 or (handle_over_time_status = 0 and task_end_time < now()) ) and cancel_status = 0 then 1 else 0 end),0) handleOverTimeNum, ifnull(sum(case when cancel_status = 1 then 1 else 0 end),0) cancelNum from supervision_store_task_${enterpriseId}
#{taskId}
group by supervision_task_id

