mapper.update:
update supervision_task_${enterpriseId}

  
    task_name = #{taskName,jdbcType=VARCHAR},
  
  
    task_grouping = #{taskGrouping,jdbcType=VARCHAR},
  
  
    task_end_time = #{taskEndTime,jdbcType=TIMESTAMP},
  
  
    reminder_time_before_end = #{reminderTimeBeforeEnd},
  

where task_parent_id = #{parentId,jdbcType=BIGINT}

update supervision_task_${enterpriseId}

  
    task_state = #{taskState},
  
  
    complete_time = #{completeTime},
  

where id = #{id,jdbcType=BIGINT}

mapper.select:
    select ,
    TIMESTAMPDIFF(second,now(),task_end_time) AS cnt ,
    TIMESTAMPDIFF(second,now(),task_start_time) AS tst
    from supervision_task_${enterpriseId}
    where supervision_handle_user_id = #{query.supervisionUserId}
    and deleted = 0 and cancel_status = 0
    
      
        
          (task_state = 0)
        
        
          ( task_state = 1)
        
        
          ( task_state = 4)
        
        
          ( cancel_status = 1)
        
      
    
    
     and task_end_time BETWEEN  #{startTime} and #{endTime}
    
    
      
        #{priority}
      
    
    
      
        #{taskGrouping}
      
    
    
      and (handle_over_time_status = #{handleOverTimeStatus} or (task_state = 0 and handle_over_time_status = 0 and task_end_time  <  now()))
    
    
      and ((handle_over_time_status = 0 AND task_state != 0) or (task_state=0 and  task_end_time > now()) )
    
    
      ORDER BY
      if(cnt >= 0, 0, 1),
      if(cnt  <  0, abs(cnt), cnt )
    
    
      ORDER BY
      if(cnt >= 0, 1,0) desc,
      if(cnt  <  0,abs(cnt), cnt ) desc
    
    
      ORDER BY
      if(tst >= 0, 0, 1),
      if(tst  <  0, abs(tst), tst )
    
    
      ORDER BY
      if(tst >= 0, 1,0) desc,
      if(tst  <  0,abs(tst), tst ) desc

mapper.update:
update supervision_task_${enterpriseId}
complete_time = now() , submit_time = now(),
